import Movies from '../components/Movies'
import { useSearchParams } from "react-router-dom"
import { SEARCH_ENDPOINT, DISCOVER_ENDPOINT } from './constants'
import { fetchMovies } from '../data/moviesSlice'
import { useDispatch, useSelector } from 'react-redux'

const Home = ({ viewTrailer, closeCard }) => {
    const state = useSelector((state) => state)
    const { movies } = state?.movies
    const dispatch = useDispatch()
    const [searchParams, setSearchParams] = useSearchParams()
    const searchQuery = searchParams.get('search')

    const getMovies = () => {
        if (searchQuery) {
            dispatch(fetchMovies(`${SEARCH_ENDPOINT}&query=${searchQuery}`))
        } else {
            dispatch(fetchMovies(DISCOVER_ENDPOINT))
        }
    }

    useEffect(() => {
        getMovies()
    }, [])

    return (
        <>                
            {movies?.results?.length === 0 && (
                <div style={{padding: "30px"}}><h6>Sorry, there are no movies available at the moment. Try another movie.</h6></div>
            )}

            <Movies movies={movies} viewTrailer={viewTrailer} closeCard={closeCard}/>
        </>
    )
}

export default Home
