import Movies from '../components/Movies'
import { useEffect, useRef } from 'react'
import { useSearchParams } from 'react-router-dom'
import { SEARCH_ENDPOINT, DISCOVER_ENDPOINT } from '../constants'
import { fetchMovies } from '../data/moviesSlice'
import { useDispatch, useSelector } from 'react-redux'
import useViewTrailer from '../hooks/useViewTrailer'
import YouTubePlayer from '../components/YouTubePlayer'
import moviesSlice from '../data/moviesSlice'
import LoadingSpinner from '../components/LoadingSpinner'

const Home = () => {
    const state = useSelector((state) => state)
    const [videoKey, viewTrailer] = useViewTrailer()
    const fetchInProgress = useRef(false); // google how to deal with async issues from useState and redux
    const { movies, pageNumber, loading } = state?.movies
    const dispatch = useDispatch()
    const [searchParams] = useSearchParams()
    const searchQuery = searchParams.get('search')
    const { incrementPageNumber } = moviesSlice.actions

    useEffect(() => {
        const handleScroll = () => {
            if (
                window.innerHeight + document.documentElement.scrollTop ===
                document.documentElement.offsetHeight && !fetchInProgress.current
            ) {
                fetchInProgress.current = true;

                if (searchQuery) {
                    dispatch(fetchMovies({
                        apiUrl: `${SEARCH_ENDPOINT}&query=${searchQuery}&page=${pageNumber + 1}`,
                        isScrolling: true
                    }))
                } else {
                    dispatch(fetchMovies({
                        apiUrl: `${DISCOVER_ENDPOINT}&page=${pageNumber + 1}`,
                        isScrolling: true
                    }))
                }
                dispatch(incrementPageNumber())
            }
        }

        window.addEventListener('scroll', handleScroll)

        // cleanup the event listener
        return () => window.removeEventListener('scroll', handleScroll)
    }, [pageNumber, loading])

    const getMovies = () => {
        if (searchQuery) {
            dispatch(fetchMovies({
                apiUrl: `${SEARCH_ENDPOINT}&query=${searchQuery}&page=${pageNumber}`,
                isScrolling: false
            }))
        } else {
            dispatch(fetchMovies({
                apiUrl: `${DISCOVER_ENDPOINT}&page=${pageNumber}`,
                isScrolling: false
            }))
        }
    }

    useEffect(() => {
        getMovies()
    }, [searchQuery])

    useEffect(() => {
        fetchInProgress.current = false
    }, [movies])

    return (
        <> 
            {videoKey && <YouTubePlayer videoKey={videoKey} />}

            {movies?.results?.length === 0 && (
                <div style={{padding: "30px"}}><h6>Sorry, there are no movies available at the moment. Try another movie.</h6></div>
            )}

            <Movies movies={movies} viewTrailer={viewTrailer}/>

            {loading && <LoadingSpinner/>}
        </>
    )
}

export default Home
