import { useEffect, useState } from 'react'
import { Routes, Route, createSearchParams, useSearchParams, useNavigate } from "react-router-dom"
import { useDispatch, useSelector } from 'react-redux'
import 'reactjs-popup/dist/index.css'
import { fetchMovies } from './data/moviesSlice'
import { SEARCH_ENDPOINT, DISCOVER_ENDPOINT } from './constants'
import Header from './components/Header'
import Movies from './components/Movies'
import Starred from './pages/Starred'
import WatchLater from './pages/WatchLater'
import YouTubePlayer from './components/YoutubePlayer'
import { getMovie } from './api/moviedb-api'
import './app.scss'
import Home from './pages/Home'

const App = () => {
  const state = useSelector((state) => state)
  const { movies } = state?.movies
  const dispatch = useDispatch()
  const [searchParams, setSearchParams] = useSearchParams()
  const searchQuery = searchParams.get('search')
  const [videoKey, setVideoKey] = useState()
  const navigate = useNavigate()

  const getSearchResults = (query) => {
    if (query !== '') {
      dispatch(fetchMovies(`${SEARCH_ENDPOINT}&query=`+query))
      setSearchParams(createSearchParams({ search: query }))
    } else {
      dispatch(fetchMovies(DISCOVER_ENDPOINT))
      setSearchParams()
    }
  }

  const searchMovies = (query) => {
    navigate('/')
    getSearchResults(query)
  }

  const getMovies = () => {
    if (searchQuery) {
        dispatch(fetchMovies(`${SEARCH_ENDPOINT}&query=${searchQuery}`))
    } else {
        dispatch(fetchMovies(DISCOVER_ENDPOINT))
    }
  }

  const viewTrailer = (movie) => {
    setVideoKey(null)
    setVideoKey(getMovie(movie.id))
  }

  useEffect(() => {
    getMovies()
  }, [])

  return (
    <div className="App">
      <Header searchMovies={searchMovies} searchParams={searchParams} setSearchParams={setSearchParams} />

      <div className="container">
        {videoKey && (
          <YouTubePlayer
            videoKey={videoKey}
          />
        )}

        <Routes>
          <Route path="/" element={<Home movies={movies} viewTrailer={viewTrailer}/>} />
          <Route path="/starred" element={<Starred viewTrailer={viewTrailer}/>} />
          <Route path="/watch-later" element={<WatchLater viewTrailer={viewTrailer} />} />
          <Route path="*" element={<h1 className="not-found">Page Not Found</h1>} />
        </Routes>
      </div>
    </div>
  )
}

export default App
