import Movies from '../components/Movies'
import { useCallback, useEffect } from 'react'
import { useSearchParams } from 'react-router-dom'
import { SEARCH_ENDPOINT, DISCOVER_ENDPOINT } from '../constants'
import { fetchMovies } from '../data/moviesSlice'
import { useDispatch, useSelector } from 'react-redux'
import useViewTrailer from '../hooks/useViewTrailer'
import YouTubePlayer from '../components/YouTubePlayer'

const Home = () => {
    const state = useSelector((state) => state)
    const [videoKey, viewTrailer] = useViewTrailer()
    const { movies } = state?.movies
    const dispatch = useDispatch()
    const [searchParams] = useSearchParams()
    const searchQuery = searchParams.get('search')
    const [pageNumber, setPageNumber] = useState(1);
    const loadingRef = useRef(null);

    const getMovies = useCallback(() => {
        if (searchQuery) {
            dispatch(fetchMovies(`${SEARCH_ENDPOINT}&query=${searchQuery}&page=${pageNumber}`))
        } else {
            dispatch(fetchMovies(`${DISCOVER_ENDPOINT}&page=${pageNumber}`))
        }
    }, [pageNumber, searchQuery])

    useEffect(() => {
        getMovies()
    }, [])

    // Infinite scroll
    useEffect(() => {
        const observer = new IntersectionObserver(
          (entries) => {
            if (entries[0].isIntersecting) {
              setPageNumber(pageNumber + 1);
            }
          },
          { threshold: 0.5 }
        );
        if (loadingRef.current) observer.observe(loadingRef.current);

        return () => {
          if (observer.current) {
            observer.unobserve(observer.current);
          }
        };
    }, [loadingRef]);

    return (
        <> 
            {videoKey && <YouTubePlayer videoKey={videoKey} />}

            {movies?.results?.length === 0 && (
                <div style={{padding: "30px"}}><h6>Sorry, there are no movies available at the moment. Try another movie.</h6></div>
            )}

            <Movies movies={movies} viewTrailer={viewTrailer}/>

            <div className="loader" ref={loadingRef}>
                <h3>Loading....</h3>
            </div>
        </>
    )
}

export default Home
