import Movies from '../components/Movies'
import { useEffect, useState } from 'react'
import { useSearchParams } from 'react-router-dom'
import { SEARCH_ENDPOINT, DISCOVER_ENDPOINT } from '../constants'
import { fetchMovies } from '../data/moviesSlice'
import { useDispatch, useSelector } from 'react-redux'
import useViewTrailer from '../hooks/useViewTrailer'
import YouTubePlayer from '../components/YouTubePlayer'

const Home = () => {
    const state = useSelector((state) => state)
    const [videoKey, viewTrailer] = useViewTrailer()
    // const [pageNumber, setPageNumber] = useState(1)
    const { movies, pageNumber, loading } = state?.movies
    const dispatch = useDispatch()
    const [searchParams] = useSearchParams()
    const searchQuery = searchParams.get('search')

    useEffect(() => {
        const handleScroll = () => {
            if (
                window.innerHeight + document.documentElement.scrollTop ===
                document.documentElement.offsetHeight && !loading
            ) {
                dispatch(fetchMovies({
                    apiUrl: `${DISCOVER_ENDPOINT}&page=${pageNumber + 1}`,
                    isScrolling: true
                }))
                setPageNumber(pageNumber + 1)
            }
        }

        window.addEventListener('scroll', handleScroll)

        // cleanup the event listener
        return () => window.removeEventListener('scroll', handleScroll)
    }, [pageNumber])

    const getMovies = () => {
        if (searchQuery) {
            dispatch(fetchMovies({
                apiUrl: `${SEARCH_ENDPOINT}&query=${searchQuery}&page=${pageNumber}`,
                isScrolling: false
            }))
        } else {
            dispatch(fetchMovies({
                apiUrl: `${DISCOVER_ENDPOINT}&page=${pageNumber}`,
                isScrolling: false
            }))
        }
    }

    useEffect(() => {
        getMovies()
    }, [])

    console.log('Home movies', movies)

    return (
        <> 
            {videoKey && <YouTubePlayer videoKey={videoKey} />}

            {movies?.results?.length === 0 && (
                <div style={{padding: "30px"}}><h6>Sorry, there are no movies available at the moment. Try another movie.</h6></div>
            )}

            <Movies movies={movies} viewTrailer={viewTrailer}/>
        </>
    )
}

export default Home
