import { useEffect, useState } from 'react'
import { Routes, Route, useSearchParams } from "react-router-dom"
import { useDispatch, useSelector } from 'react-redux'
import 'reactjs-popup/dist/index.css'
import { fetchMovies } from './data/moviesSlice'
import { SEARCH_ENDPOINT, DISCOVER_ENDPOINT } from './constants'
import Layout from './components/Layout'
import Starred from './pages/Starred'
import WatchLater from './pages/WatchLater'
import YouTubePlayer from './components/YoutubePlayer'
import { getMovie } from './api/moviedb-api'
import './app.scss'
import Home from './pages/Home'

const App = () => {
  const state = useSelector((state) => state)
  const { movies } = state?.movies
  const dispatch = useDispatch()
  const [searchParams, setSearchParams] = useSearchParams()
  const searchQuery = searchParams.get('search')
  const [videoKey, setVideoKey] = useState()

  const getMovies = () => {
    if (searchQuery) {
        dispatch(fetchMovies(`${SEARCH_ENDPOINT}&query=${searchQuery}`))
    } else {
        dispatch(fetchMovies(DISCOVER_ENDPOINT))
    }
  }

  const viewTrailer = async (movie) => {
    setVideoKey(null)
    setVideoKey(await getMovie(movie.id))
  }

  useEffect(() => {
    getMovies()
  }, [])

  return (
    <Layout>
      {videoKey && (
        <YouTubePlayer
          videoKey={videoKey}
        />
      )}

      <Routes>
        <Route path="/" element={<Home movies={movies} viewTrailer={viewTrailer}/>} />
        <Route path="/starred" element={<Starred viewTrailer={viewTrailer}/>} />
        <Route path="/watch-later" element={<WatchLater viewTrailer={viewTrailer} />} />
        <Route path="*" element={<h1 className="not-found">Page Not Found</h1>} />
      </Routes>
    </Layout>
  )
}

export default App
